package model.entities;import java.time.LocalDate;import java.time.format.DateTimeFormatter;import java.util.Objects;import lombok.Getter;import lombok.Setter;import model.entities.enums.Category;@Getter@Setterpublic class Event {    private static final DateTimeFormatter FORMATTER = DateTimeFormatter.ofPattern("dd/MM/yyyy");    private String name;    private String cep;    private Category category;    private LocalDate eventDate;    private String description;    private City city;    public Event(LocalDate eventDate) {        this.eventDate = eventDate;    }    public Event(String name, String cep, Category category, LocalDate date, String description, City city) {        this.name = name;        this.cep = cep;        this.category = category;        this.eventDate = date;        this.description = description;        this.city = city;    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        Event event = (Event) o;        if (!Objects.equals(name, event.name)) return false;        return Objects.equals(eventDate, event.eventDate);    }    @Override    public int hashCode() {        int result = name != null ? name.hashCode() : 0;        result = 31 * result + (eventDate != null ? eventDate.hashCode() : 0);        return result;    }    @Override    public String toString() {        return "Event{" +                "name='" + name + '\'' +                ", cep='" + cep + '\'' +                ", category=" + category +                ", date=" + FORMATTER.format(eventDate) +                ", description='" + description + '\'' +                ", city=" + city +                '}';    }}